mapper.select:
select * from SQUENCE_INFO where 1=1
and openid = #{openid}
and cardnumber = #{cardnumber}
and status=1 and to_char(createtime,'yyyy-MM-dd')=to_char(#{nowdate},'yyyy-MM-dd')

select max(i.squence) from SQUENCE_INFO i where i.isappointment=#{isappointment}
and to_char(i.createtime,'yyyy-MM-dd')=to_char(#{nowdate},'yyyy-MM-dd')
and i.deptid = #{deptid}

    and i.isearly = #{isearly}

select t.squence as queueNumber ,t.cardnumber as name,t.deptid,t.status state from SQUENCE_INFO t where to_char(t.createtime,'yyyy-MM-dd')=to_char(#{nowdate},'yyyy-MM-dd')
and t.deptid=#{deptid}
and t.status=2 order by squence desc

select t.squence as queueNumber ,t.cardnumber as name,t.deptid,t.status state from SQUENCE_INFO t where
to_char(t.createtime,'yyyy-MM-dd')=to_char(#{nowdate},'yyyy-MM-dd')

    and t.deptid=#{deptid}

and t.status=#{status}

    order by squence desc


    order by squence asc

select t.id,t.squence from squence_info t where t.isappointment=#{isappointment}
and t.status=1
and to_char(t.createtime,'yyyy-MM-dd')=to_char(#{nowdate},'yyyy-MM-dd')
and t.deptid =#{deptid}

    and t.isearly = #{isearly}


    and t.timequantum <= #{nowtimequan}

and t.squence=
(select min(t1.squence) from squence_info t1 where t1.isappointment=#{isappointment}
and to_char(t1.createtime,'yyyy-MM-dd')=to_char(#{nowdate},'yyyy-MM-dd')
and t1.status=1
and t1.deptid =#{deptid}

    and t1.isearly = #{isearly}


    and t1.timequantum <= #{nowtimequan}

)

mapper.update:
update SQUENCE_INFO t
set
t.isexport=0

    ,t.status = #{status}


    , t.cardnumber = t.cardnumber ||'@'||#{winNumber}


where ID = #{id}
